mapper.select:

select hsdd_id, hsdt_id, hsdd_name, int_value, str_value, parent_id, sort_num
from hzs_dictionary_detail
where hsdd_id = #{hsddId,jdbcType=VARCHAR}

        
        select hsdd_id, hsdt_id, hsdd_name, int_value, str_value, parent_id, sort_num
        from hzs_dictionary_detail
        
            hsdt_id = #{hsdtId, jdbcType=VARCHAR}
            
                and hsdd_name like concat('%',#{hsddName, jdbcType=VARCHAR},'%')
            
            
                and parent_id = #{parentId, jdbcType=VARCHAR}
            

        
        order by sort_num

select count(*)
from hzs_dictionary_detail
where hsdt_id = #{hsdtId, jdbcType=VARCHAR}
and hsdd_name = #{hsddName, jdbcType=VARCHAR}

    and hsdd_id != #{hsddId,jdbcType=VARCHAR}
