mapper.update:

update hzs_user
set delete_flag = -1
where hsu_id = #{hsuId,jdbcType=VARCHAR}


update hzs_user
set
delete_flag = #{deleteFlag,jdbcType=INTEGER}
where hsu_id = #{hsuId,jdbcType=VARCHAR}


update hzs_user
set
hsu_password = #{newPassword,jdbcType=INTEGER}
where hsu_id = #{hsuId,jdbcType=VARCHAR}
